opam.opam:
opam-version: "2.0"
synopsis: "Bindings to the Perl Compatibility Regular Expressions library"
description: """
pcre-ocaml offers library functions for string pattern matching and
substitution, similar to the functionality offered by the Perl language."""
maintainer: "Markus Mottl <markus.mottl@gmail.com>"
authors: "Markus Mottl <markus.mottl@gmail.com>"
license: "LGPL-2.1+ with OCaml linking exception"
homepage: "https://mmottl.github.io/pcre-ocaml"
doc: "https://mmottl.github.io/pcre-ocaml/api"
bug-reports: "https://github.com/mmottl/pcre-ocaml/issues"
depends: [
  "ocaml" {>= "4.04"}
  "dune" {build & >= "1.4.0"}
  "conf-libpcre" {build}
  "base" {build}
  "base-bytes"
]
build: [
  ["dune" "subst"] {pinned}
  ["dune" "build" "-p" name "-j" jobs]
]
dev-repo: "git+https://github.com/mmottl/pcre-ocaml.git"

opam-version: "2.0"
name: "conf-pkg-config"
version: "1.1"
synopsis: "Virtual package relying on pkg-config installation"
description: """
This package can only install if the pkg-config package is installed
on the system."""
maintainer: "unixjunkie@sdf.org"
authors: "Francois Berenger"
license: "GPL"
homepage: "http://www.freedesktop.org/wiki/Software/pkg-config/"
bug-reports: "https://github.com/ocaml/opam-repository/issues"
flags: light-uninstall
build: ["pkg-config" "--help"]
install:
  ["ln" "-s" "/usr/local/bin/pkgconf" "%{bin}%/pkg-config"] {os = "openbsd"}
remove: ["rm" "-f" "%{bin}%/pkg-config"] {os = "openbsd"}
post-messages:
  "conf-pkg-config: A symlink to /usr/local/bin/pkgconf has been installed in the OPAM bin directory (%{bin}%) on your PATH as 'pkg-config'. This is necessary for correct operation."
    {os = "openbsd"}
depexts: [
  ["pkg-config"] {os-distribution = "debian"}
  ["pkg-config"] {os-distribution = "ubuntu"}
  ["pkg-config"] {os-distribution = "archlinux"}
  ["pkgconfig"] {os-distribution = "fedora"}
  ["pkgconfig"] {os-distribution = "centos"}
  ["pkgconfig"] {os-distribution = "mageia"}
  ["pkgconfig"] {os-distribution = "rhel"}
  ["pkgconfig"] {os-distribution = "oraclelinux"}
  ["pkgconfig"] {os-distribution = "alpine"}
  ["devel/pkgconf"] {os = "freebsd"}
  ["devel/pkgconf"] {os = "openbsd"}
  ["pkg-config"] {os = "macos" & os-distribution = "homebrew"}
  ["pkgconf"] {os = "freebsd"}
]

opam-version: "2.0"
name: "conf-m4"
version: "1"
synopsis: "Virtual package relying on m4"
description:
  "This package can only install if the m4 binary is installed on the system."
maintainer: "tim@gfxmonk.net"
authors: "GNU Project"
license: "GPL-3"
homepage: "http://www.gnu.org/software/m4/m4.html"
bug-reports: "https://github.com/ocaml/opam-repository/issues"
build: ["sh" "-exc" "echo | m4"]
depexts: [
  ["m4"] {os-distribution = "debian"}
  ["m4"] {os-distribution = "ubuntu"}
  ["m4"] {os-distribution = "fedora"}
  ["m4"] {os-distribution = "rhel"}
  ["m4"] {os-distribution = "centos"}
  ["m4"] {os-distribution = "alpine"}
  ["m4"] {os-distribution = "nixos"}
  ["m4"] {os-distribution = "opensuse"}
  ["m4"] {os-distribution = "oraclelinux"}
  ["m4"] {os-distribution = "archlinux"}
]

opam-version: "2.0"
name: "conf-libpcre"
version: "1"
synopsis: "Virtual package relying on a libpcre system installation"
description:
  "This package can only install if the libpcre is installed on the system."
maintainer: "blue-prawn"
authors: ["Philip Hazel" "Zoltan Herczeg"]
license: "BSD-3-Clause"
homepage: "http://www.pcre.org/"
bug-reports: "https://github.com/ocaml/opam-repository/issues"
depends: [
  "conf-pkg-config" {build}
]
build: ["pkg-config" "libpcre"]
depexts: [
  ["libpcre3-dev"] {os-distribution = "debian"}
  ["libpcre-devel"] {os-distribution = "mageia"}
  ["libpcre3-dev"] {os-distribution = "ubuntu"}
  ["pcre-devel"] {os-distribution = "centos"}
  ["pcre-devel"] {os-distribution = "fedora"}
  ["pcre-devel"] {os-distribution = "rhel"}
  ["pcre-dev"] {os-distribution = "alpine"}
  ["pcre-devel"] {os-distribution = "opensuse"}
  ["pcre"] {os = "freebsd"}
  ["pcre"] {os = "macos" & os-distribution = "homebrew"}
]
dev-repo: "git+https://github.com/mmottl/pcre-ocaml.git"

